sys_script_client.script:
function onSubmit() {
    // Get the value of the short_description field
    var shortDescription = g_form.getValue('short_description');

    // Define the regular expression to match alphanumeric characters and spaces
    var regex = /^[a-zA-Z0-9 ]*$/;

    // Validate the short_description field value against the regex
    if (!regex.test(shortDescription)) {
        // If the value does not match the regex, display an error message
        g_form.addErrorMessage('The Short Description field can only contain alphanumeric characters and spaces.');

        // Prevent the form from being submitted
        return false;
    }

    // Allow the form to be submitted
    return true;
}
