sys_script_client.script:
function onLoad() {
    // Check if the user has the 'itil' role
    if (g_user.hasRole('itil')) {
        // Display an informational message if the user has the 'itil' role
        g_form.addInfoMessage('Welcome, ITIL user! You have the necessary permissions to manage incidents.');
    } else {
        // Display an informational message if the user does not have the 'itil' role
        g_form.addInfoMessage('You do not have the ITIL role. Your access to certain actions may be limited.');
    }
}
