sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Use the value of the 'category' field to determine what to autofill
    if (newValue == 'hardware') {
        g_form.setValue('subcategory', 'computer');
        g_form.setValue('description', 'Hardware-related incident involving a computer.');
    } else if (newValue == 'software') {
        g_form.setValue('subcategory', 'application');
        g_form.setValue('description', 'Software-related incident involving an application.');
    } else if (newValue == 'network') {
        g_form.setValue('subcategory', 'connectivity');
        g_form.setValue('description', 'Network-related incident involving connectivity issues.');
    } else {
        // Clear the fields if the category doesn't match any predefined condition
        g_form.clearValue('subcategory');
        g_form.clearValue('description');
    }
}
