sys_script_client.script:
function onLoad() {
    displayPriorityWarning();
}

function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }
    displayPriorityWarning();
}

function displayPriorityWarning() {
    // Clear any existing info messages
    g_form.clearMessages();

    // Get the value of the priority field
    var priority = g_form.getValue('priority');

    // Display a warning message based on the priority value
    if (priority == '1') {  // Assuming '1' is the highest priority
        g_form.addInfoMessage('Warning: High priority incident! Please take immediate action.');
    } else if (priority == '2') {
        g_form.addInfoMessage('Alert: Medium priority incident. Please address this as soon as possible.');
    } else if (priority == '3') {
        g_form.addInfoMessage('Notice: Low priority incident. Please address this when possible.');
    }
}
