sys_script_include.script:
var CIDetailsFetcher = Class.create();
CIDetailsFetcher.prototype = {
    initialize: function() {},

    getCIDetails: function(ciSysId) {
        var ciDetails = {};
        var gr = new GlideRecord('cmdb_ci');
        if (gr.get(ciSysId)) {
            ciDetails = {
                sys_id: gr.getValue('sys_id'),
                name: gr.getValue('name'),
                category: gr.getValue('category'),
                subcategory: gr.getValue('subcategory'),
                location: gr.getValue('location'),
                operational_status: gr.getValue('operational_status')
            };
        } else {
            ciDetails.error = 'CI not found';
        }
        return ciDetails;
    },

    type: 'CIDetailsFetcher'
};
