sys_script_include.script:
var IncidentDateDifference = Class.create();
IncidentDateDifference.prototype = {
    initialize: function() {},

    getDateDifference: function(incidentSysId) {
        var gr = new GlideRecord('incident');
        if (gr.get(incidentSysId)) {
            var createdDate = new GlideDateTime(gr.getValue('sys_created_on'));
            var currentDate = new GlideDateTime();

            // Calculate the difference in milliseconds
            var differenceInMs = currentDate.getNumericValue() - createdDate.getNumericValue();

            // Convert milliseconds to days
            var differenceInDays = Math.floor(differenceInMs / (1000 * 60 * 60 * 24));

            return differenceInDays;
        } else {
            return 'Incident not found';
        }
    },

    type: 'IncidentDateDifference'
};
