sys_script_include.script:
var IncidentStateUpdater = Class.create();
IncidentStateUpdater.prototype = {
    initialize: function() {},

    updateIncidentState: function(incidentSysId, newState) {
        var gr = new GlideRecord('incident');
        if (gr.get(incidentSysId)) {
            gr.setValue('state', newState);
            var success = gr.update();
            if (success) {
                gs.addInfoMessage('Incident ' + incidentSysId + ' state has been updated to ' + newState);
                return true;
            } else {
                gs.addErrorMessage('Failed to update incident ' + incidentSysId);
                return false;
            }
        } else {
            gs.addErrorMessage('Incident with Sys ID ' + incidentSysId + ' not found');
            return false;
        }
    },

    type: 'IncidentStateUpdater'
};
